import  React, {Component} from 'react';
import axios from 'axios';

import './css/style.css';
import logo from './img/logo.png';


export default  class adminViewVehicle extends  Component{


    constructor(props){
        super(props);

        this.onChangeSearch = this.onChangeSearch.bind(this);

       
        this.state = {vehicle : [], search:''};
    }

    componentDidMount() {
       
        axios.get('http://localhost:4000/rental/admiviewvehicle/')
            .then(response => {
                this.setState({orders : response.data});
            })
            .catch(function (error){
                console.log(error);
            })
    }

    onChangeId(e){
        this.setState( {
           id: e.target.value
        });
    }
    onChangeType(e){
        this.setState( {
            type: e.target.value
        });
    }
    onChangeManufacturer(e){
        this.setState( {
            manufacturer: e.target.value
        });
    }
    onChangeYear(e){
        this.setState( {
            year: e.target.value
        });
    }
    onChangePassengers(e){
        this.setState( {
            passengers: e.target.value
        });
    }
    onChangePrice(e){
        this.setState( {
            price: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        this.state.status = "Available";
        alert(this.state.status);
        const obj = {
            id : this.state.id,
            type : this.state.type,
            manufacturer : this.state.manufacturer,
            year : this.state.year,
            passengers : this.state.passengers,
            price : this.state.price,
            status : this.state.status
        };

       
       
                        axios.post('http://localhost:4000/rental/adminaddvehicle',obj)
                        .then(res => {
                            alert("Vehicle Add Successfully");
                            this.setState({
                                id: '',
                                type: '',
                                manufacturer:'',
                                year:'',
                                passengers:'',
                                price:''
                    
                            })
                            console.log(res.data)});
                        window.location.replace('/adminViewVehicle');
                    
    }

    render() {
        return(
            <div>
                <div class="sidebar">
                        <center>
                            <img src = {logo} />
                        
                            <a href="/">Employee</a>
                            <a href="/">Customer</a>
                            <a href="/">Supplier</a>
                            <a href="/">Rentals</a>
                            <a href="/">Spare Parts</a>
                            <a href="/">Inventory</a>
                            <a href="/">Payment</a>
                            <a href="/">Utility Bills</a>
                        </center>
                </div>

                <div class="content">
                    <div className='top'>
                        <a href = "">LogOut</a>
                        <img src = {logo} width="80"/>
                    </div>
                    <p>VEHICLE MANAGEMENT</p>
                    <div className="container" style={{marginTop:10}}>
                        
                    </div>
                </div>
            </div>
        )
    }
}
