import  React, {Component} from 'react';
import axios from 'axios';

import './css/style.css';
import logo from './img/logo.png';


export default  class adminAddVehicle extends  Component{


    constructor(props){
        super(props);

        this.onChangeId = this.onChangeName.bind(this);
        this.onChangeType = this.onChangeArrivalTime.bind(this);
        this.onChangeManufacturer = this.onChangeDeparcherTime.bind(this);
        this.onChangeYear = this.onChangeStation.bind(this);
        this.onChangePassengers = this.onChangeStation.bind(this);
        this.onChangePrice = this.onChangeStation.bind(this);
        
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            arrival: '',
            deparcher:'',
            station:''
        }

        this.state.station = this.props.match.params.id;
    }
    onChangeName(e){
        this.setState( {
           name: e.target.value
        });
    }
    onChangeArrivalTime(e){
        this.setState( {
            arrival: e.target.value
        });
    }
    onChangeDeparcherTime(e){
        this.setState( {
            deparcher: e.target.value
        });
    }
    onChangeStation(e){
        this.setState( {
            station: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            name : this.state.name,
            arrival : this.state.arrival,
            deparcher : this.state.deparcher,
            station : this.state.station
        };

       
       
                        axios.post('http://localhost:4000/trainFoodAdmin/adminaddtrain',obj)
                        .then(res => {
                            alert("Train Add Successfully");
                            this.setState({
                                name :'',
                                arrival :'',
                                deparcher :'',
                                station:''
                    
                            })
                            console.log(res.data)});
                        // this.props.history.push('/signIn');
                        window.location.replace('/adminviewtrain/'+this.state.station);
                    
    }

    render() {
        return(
            <div>
                <div class="sidebar">
                        <center>
                            <img src = {logo} />
                        
                            <a href="/">Employee</a>
                            <a href="/">Customer</a>
                            <a href="/">Supplier</a>
                            <a href="/">Rentals</a>
                            <a href="/">Spare Parts</a>
                            <a href="/">Inventory</a>
                            <a href="/">Payment</a>
                            <a href="/">Utility Bills</a>
                        </center>
                </div>

                <div class="content">
                    <div className='top'>
                        <a href = "">LogOut</a>
                        <img src = {logo} width="80"/>
                    </div>
                    <p>ADD NEW VEHICLE</p>
                    <div className="container" style={{marginTop:10}}>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label>Vehicle Id :</label>
                                <input type ="text" required  className="form-control" value={this.state.id} onChange = {this.onChangeId}/>
                            </div>
                            <div className="form-group">
                                <label>Vehicle Type :</label>
                                {/* <input type ="time" required className="form-control" value={this.state.arrival} onChange = {this.onChangeArrivalTime}/> */}
                                <select required className="form-control" value={this.state.type} onChange = {this.onChangeType}>
                                    <option>select vehicle type....</option>
                                    <option value = "Car">Car</option>
                                    <option value = "Van">Van</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Manufacturer :</label>
                                <input type ="text" required className="form-control" value={this.state.manufacturer} onChange = {this.onChangeManufacturer}/>
                            </div>
                            <div className="form-group">
                                <label>Manufactured Year :</label>
                                <input type ="number"  value={this.state.year} onChange = {this.onChangeYear} className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label>Max Passengers :</label>
                                <input type ="number"  min = "2" value={this.state.passengers} onChange = {this.onChangePassengers} className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label>Rental Price :</label>
                                <input type ="number"  value={this.state.price} onChange = {this.onChangePrice} className="form-control"/>
                            </div>
                        
                            <div className="form-group">
                                <input type = "submit" value = "Add Vehicle" className="btn btn-info"/>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
